#include <algorithm>
#include "leetcode.h"

class Solution {
public:
  vector<int> findSubstring(string S, vector<string> &L) {
    vector<int> ans;
    const int nwords = L.size();
    if (nwords == 0) return ans;

    unordered_map<string, int> toFind, hasFind;
    for (auto &w : L) {
      ++toFind[w];
    }

    const int word_size = L[0].size(); // words are the same length
    const int window_size = word_size * nwords;
    const int N = S.size();
    for(int i = 0; i < word_size; ++i) {
      hasFind.clear();
      int count = 0;
      int b = i;
      // find vaild window from each b=i, i+w, i+2w...
      for(int j = i; j < N; j += word_size) {
        string word = S.substr(j, word_size);
        if ( toFind[word] == 0 ) {
          // no such word in L, reset counter and increment start point
          hasFind.clear();
          count = 0;
          b = j + word_size;
        } else if (hasFind[word] < toFind[word]) {
          // found a valid word
          ++hasFind[word];
          ++count;
        } else {
          // already reached required amount of this word
          // move start point to remove first excess word
          // such that hasFind[word] = toFind[word] - 1;
          string w;
          while ( (w = S.substr(b, word_size)) != word) {
            --hasFind[w];
            --count;
            b += word_size;
          }
          b += word_size;
        }

        if (count == nwords) {
          // found valid window, increment start point and word
          ans.push_back(b);
          --hasFind[S.substr(b, word_size)];
          --count;
          b += word_size;
        }
      } // for j
    } // for i
    return ans;
  }
};

